{
  "title": "DDR5 Same Bank Refresh Specification",
  "description": {
    "overview": "The DDR5 specification outlines conditions under which a same-bank refresh (REFsb) command can be issued, including timing constraints and restrictions. The global refresh counter increments upon issuing REFab or after all banks have received their one REFsb command with synchronization reset to zero.",
    "conditions": [
      {
        "condition_type": "tRFC1",
        "description": "Must be satisfied following a prior 1x REFab command."
      },
      {
        "condition_type": "tRFC2",
        "description": "Must be satisfied following a prior 2x REFab commands.",
        "note": "Specific to the number of preceding REFab operations, indicating increased refresh frequency requirements for subsequent same-bank refreshing."
      },
      {
        "condition_type01",
        "description": "Must be satisfied after a prior REFsb command.",
        "note": "This ensures that the memory is stable before issuing another REFsb, preventing potential data corruption due to simultaneous refresh operations."
      },
      {
        "condition_type02",
        "description": "Must be satisfied after a prior PRECHARGE command.",
        "note": "This condition ensures that the memory is in an appropriate state for refreshing, with proper initialization beforehand."
      },
      {
        "condition_type03",
        "description": "tRP must be satisfied after a prior PRECHARGE command to that bank.",
        "note": "This condition relates the timing of precharge operations and their impact on refresh cycles, ensuring proper memory state transitions."
      },
      {
        "condition_type04",
        "description": "tRRD_L must be satisfied after a prior ACTIVATE command from another bank in the same group.",
        "note": "This condition is necessary for activating different banks within the same memory module, ensuring that refresh cycles do not interfere with active operations."
      }
    ],
    "restrictions": [
      {
        "condition_type05",
        "description": "tFAW has not been met.",
        "note": "This restriction prevents the issuance of REFsb commands when an activate window timing violation (ACTIVATE command) is imminent, ensuring data integrity."
      }
    ],
    "post_refresh_state": {
      "description": "Once a same-bank refresh cycle has completed, banks are in idle state and accessible for normal operations.",
      "note": "This indicates that the memory module is ready to handle new commands after completing its own internal refreshing process."
    }
  },
  "source_pages": ["JEDEC Standard No. 79-5"],
  "confidence": "High"
}

